package p01.op_unary;

public class DenyLogicOperatorEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		boolean play = true;
		System.out.println(play);
		
		play = !play;	// play값   false
		System.out.println(play);

		play = !play;	// play값  true
		System.out.println(play);
	}

}
